/* Focus indicators for accessibility */

/* Base focus styles - fallback for older browsers */
button:focus,
input:focus,
select:focus,
textarea:focus,
a:focus,
[role="button"]:focus,
[role="link"]:focus,
[tabindex]:focus {
  outline: 2px solid #0066cc;
  outline-offset: 2px;
}

/* Modern focus-visible for better UX - only shows for keyboard navigation */
button:focus-visible,
input:focus-visible,
select:focus-visible,
textarea:focus-visible,
a:focus-visible,
[role="button"]:focus-visible,
[role="link"]:focus-visible,
[tabindex]:focus-visible {
  outline: 2px solid #0066cc;
  outline-offset: 2px;
  border-radius: 4px;
}

/* Remove default focus outline when focus-visible is supported */
button:focus:not(:focus-visible),
input:focus:not(:focus-visible),
select:focus:not(:focus-visible),
textarea:focus:not(:focus-visible),
a:focus:not(:focus-visible),
[role="button"]:focus:not(:focus-visible),
[role="link"]:focus:not(:focus-visible),
[tabindex]:focus:not(:focus-visible) {
  outline: none;
}

/* High contrast focus for better visibility on dark backgrounds */
.dark-theme button:focus-visible,
.dark-theme input:focus-visible,
.dark-theme select:focus-visible,
.dark-theme textarea:focus-visible,
.dark-theme a:focus-visible,
.dark-theme [role="button"]:focus-visible,
.dark-theme [role="link"]:focus-visible,
.dark-theme [tabindex]:focus-visible {
  outline-color: #66b3ff;
}

/* Custom focus styles for specific button variants */
.btn-primary:focus-visible {
  outline-color: #ffffff;
  outline-offset: 3px;
}

.btn-secondary:focus-visible {
  outline-color: #0066cc;
  outline-offset: 2px;
}

/* Focus styles for form elements */
input[type="text"]:focus-visible,
input[type="email"]:focus-visible,
input[type="password"]:focus-visible,
textarea:focus-visible {
  outline: 2px solid #0066cc;
  outline-offset: 1px;
  border-color: #0066cc;
}

/* Focus styles for checkboxes and radio buttons */
input[type="checkbox"]:focus-visible,
input[type="radio"]:focus-visible {
  outline: 2px solid #0066cc;
  outline-offset: 2px;
}

/* Ensure focus is visible on custom interactive components */
[data-interactive]:focus-visible,
.interactive:focus-visible {
  outline: 2px solid #0066cc;
  outline-offset: 2px;
  border-radius: 4px;
}

/* Reduced motion preference */
@media (prefers-reduced-motion: no-preference) {
  button:focus-visible,
  input:focus-visible,
  select:focus-visible,
  textarea:focus-visible,
  a:focus-visible,
  [role="button"]:focus-visible,
  [role="link"]:focus-visible,
  [tabindex]:focus-visible {
    transition: outline-color 0.2s ease-in-out;
  }
}

